var link=document.querySelector(".letter-button"),popup=document.querySelector(".modal-window"),close=popup.querySelector(".close-button"),login=popup.querySelector("[name=user-name]");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),login.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show")});
